import tape from 'tape-catch';
import parseSegments from '../../parser/segments';
tape('PARSER / segments parser', function (assert) {
  var segments = parseSegments([{
    matcherGroup: {
      combiner: 'AND',
      matchers: [{
        matcherType: 'IN_SEGMENT',
        userDefinedSegmentMatcherData: {
          segmentName: 'A'
        }
      }, {
        matcherType: 'IN_SEGMENT',
        userDefinedSegmentMatcherData: {
          segmentName: 'B'
        }
      }]
    }
  }]);
  assert.ok(segments.has('A'));
  assert.ok(segments.has('B'));
  assert.end();
});