import tape from 'tape-catch';
import fs from 'fs';
import rl from 'readline';
import utils from '../../../engine/murmur3/murmur3';
import { hash128 } from '../../../engine/murmur3/murmur3_128';
['murmur3-sample-v4.csv', 'murmur3-sample-v3.csv', 'murmur3-sample-data-v2.csv', 'murmur3-sample-data-non-alpha-numeric-v2.csv', 'murmur3-sample-double-treatment-users.csv'].forEach(function (filename) {
  tape('MURMUR3 32 / validate hashing behavior using sample data', function (assert) {
    var parser = rl.createInterface({
      terminal: false,
      input: fs.createReadStream(require.resolve("../mocks/".concat(filename)))
    });
    parser.on('line', function (line) {
      var parts = line.toString('utf8').split(',');

      if (parts.length === 4) {
        var _parts = _slicedToArray(parts, 4),
            seed = _parts[0],
            key = _parts[1],
            hash = _parts[2],
            bucket = _parts[3];

        seed = parseInt(seed, 10);
        hash = parseInt(hash, 10);
        bucket = parseInt(bucket, 10);
        assert.equal(utils.hash(key, seed), hash);
        assert.equal(utils.bucket(key, seed), bucket);
      }
    }).on('close', assert.end);
  });
});

function dec2hex(str) {
  var sum = [];
  var dec = str.toString().split('');
  var hex = [];

  while (dec.length) {
    var s = 1 * dec.shift();

    for (var i = 0; s || i < sum.length; i++) {
      s += (sum[i] || 0) * 10;
      sum[i] = s % 16;
      s = (s - sum[i]) / 16;
    }
  }

  while (sum.length) {
    hex.push(sum.pop().toString(16));
  }

  return hex.join('');
}

['murmur3_64_uuids.csv'].forEach(function (filename) {
  tape('MURMUR3 128 / validate hashing behavior using sample data', function (assert) {
    var parser = rl.createInterface({
      terminal: false,
      input: fs.createReadStream(require.resolve("../mocks/".concat(filename)))
    });
    parser.on('line', function (line) {
      var parts = line.split(',');

      if (parts.length === 3) {
        var _parts2 = _slicedToArray(parts, 3),
            key = _parts2[0],
            seed = _parts2[1],
            hash = _parts2[2];

        seed = parseInt(seed, 10);
        var result = hash128(key, seed);
        assert.equal(result.substring(0, 16), dec2hex(hash).padStart(16, '0'));
      }
    }).on('close', assert.end);
  });
});